$(".hover-show").mouseover(function(){$(".hover-window").show(500).mouseleave(function(){$(this).hide(500)})});$(".change-skin").click(function(){$("#show-skin").slideDown();$("#wrap-skin").show().click(function(a){if(a.target.id==="wrap-skin"){$(this).slideUp().children("#show-skin").slideUp()}})});$(".skin-up").click(function(){$("#wrap-skin").slideUp().children().slideUp()});